package sec4;

import java.util.StringTokenizer;

public class StringEx2 {
    public static void main(String[] args) {
        String mem1 = "ㄱ/ㄴ/ㄷ/ㄹ/ㅁ";
        String[] m1 = mem1.split("/");
        for (String name:m1){
            System.out.println(name);
        }
        String mem2 = "ㄱㄱ&ㄴㄴ,ㄷㄷ,ㄹㄹ-ㅁㅁ";
        String[] m2 = mem2.split("&|,|-");
        for (String name:m2){
            System.out.println(name);
        }
        String mem3 = "ㄱㄱㄱ/ㄴㄴㄴ/ㄷㄷㄷ/ㄹㄹㄹ/ㅁㅁㅁ";
        StringTokenizer em3 = new StringTokenizer(mem3, "/");
        int cnt = em3.countTokens();
        for(int i = 0; i<cnt;i++){
            String token = em3.nextToken();
            System.out.println(token                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       );

        }
    }
}
